import { createSlice } from "@reduxjs/toolkit";

// const initialState = {}
export const eventSlice = createSlice({
    name: 'event',

    reducers: {
        eventAdded(state, action){
            state.push(action.payload)
        }
    }
})

export const {eventAdded} = eventSlice.actions

export default eventSlice.reducer
