import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setName, selectName, } from './profileSlice';


export function Profile(){
    const [name, setUpdateName] = useState('')
    const profileName = useSelector(selectName)

    const dispatch = useDispatch()
    return (
        <div>
        Enter your name:
        {/* <div>{profileName}</div> */}
            <div className=''>
              <label className="form-label">Name</label>
                <input className="form-control" value={name} onChange={(e)=>setUpdateName(e.target.value)} />
                <button className='btn btn-primary' onClick={()=> dispatch(setName(name))}>Add</button>
            </div>
        </div>
    )
}
